contents.text:
        unsigned char key[],  /* the RDATA part of the KEY RR */
        unsigned int keysize, /* the RDLENGTH */
        )
{
long int    ac;    /* assumed to be 32 bits or larger */

for ( ac = 0, i = 0; i < keysize; ++i )
    ac += (i&1) ? key[i] : key[i]<<8;
ac += (ac>>16) & 0xFFFF;
return ac & 0xFFFF;
}
