/*
** Multiplication.  Allows overflow.  Straightforward implementation
** of Algorithm 4.3.1.M (p.268) from [KNUTH].
*/
u_int64_t
OWPnum64_mul(u_int64_t x, u_int64_t y)
{
        unsigned long w[4];
        u_int64_t xdec[2];
        u_int64_t ydec[2];

xdec[0] = MASK32(x);
xdec[1] = MASK32(x>>32);
ydec[0] = MASK32(y);
ydec[1] = MASK32(y>>32);

for (j = 0; j < 2; j++) {
        k = 0;
        for (i = 0; ; ) {
                t = k + (xdec[i]*ydec[j]) + w[i + j];
                w[i + j] = t%EXP2POW32;
                k = t/EXP2POW32;
                if (++i < 2)
                        continue;
                else {
                        w[j + 2] = k;
                        break;
                }
        }
}

        ret = w[2];
        ret <<= 32;
        return w[1] + ret;
}

