routing-options {
   static {
       /* EXAMPLE ATTACK SOURCE */
       route 10.11.12.66/32 {
           next-hop 192.0.2.1;
           resolve;
           tag 666;
       }
       /* EXAMPLE ATTACK DESTINATION */
       route 10.128.0.2/32 {
           next-hop 192.0.2.1;
           resolve;
           tag 666;
       }
   }
   autonomous-system 100;
}

protocols {
   bgp {
       group ibgp {
           type internal;
           export rtbh;
           neighbor 172.16.0.2;
       }
   }
}
      policy-options {
   policy-statement rtbh {
       term black-hole-filter {
           from {
               tag 666;
               route-filter 0.0.0.0/0 prefix-length-range /32-/32;
           }
           then {
               local-preference 200;
               origin igp;
               community set black-hole;
               community add no-export;
               next-hop 192.0.2.1;
               accept;
           }
       }
   }
   community black-hole members 100:666;
   community no-export members no-export;
}

policy-statement black-hole-filter {
   from {
       protocol bgp;
       as-path LocalOnly;
       community black-hole;
       route-filter 0.0.0.0/0 prefix-length-range /32-/32;
   }
   then {
       community set no-export;
       next-hop 192.0.2.1;
   }
}
community black-hole members 100:666;
community no-export members no-export;

routing-options {
   forwarding-table {
       unicast-reverse-path feasible-paths;
   }
   static {
       route 192.0.2.1/32 discard;
   }
}
      interfaces {
   xe-1/0/0 {
       vlan-tagging;
       mtu 9192;
       unit 201 {
           vlan-id 201;
           family inet {
               rpf-check;
               address 10.11.12.1/24;
           }
       }
   }
}
