SecurityCategory { SECURITY-CATEGORY:Supported }::= SEQUENCE {
  type  [0] IMPLICIT SECURITY-CATEGORY.&id({Supported}),
  value [1] EXPLICIT SECURITY-CATEGORY.&Type
                                   ({Supported}{@type})
}

