container outer {
    presence 'Presence of "outer" means something.';
    container c1 {
        leaf foo {
            type uint8;
            default 1;
        }
    }
    container c2 {
        leaf-list bar {
            type uint8;
            min-elements 0;
        }
    }
    container c3 {
        leaf baz {
            type uint8;
            mandatory true;
        }
    }
}

module example1 {
    namespace "http://example.com/ns/example1";
    prefix ex1;
    typedef vowels {
        type string {
            pattern "[aeiouy]*";
        }
    }
    grouping "grp1" {
        leaf "void" {
            type "empty";
        }
    }
    container "cont" {
        leaf foo {
            type vowels;
        }
        uses "grp1";
    }
}

module example2 {
    namespace "http://example.com/ns/example2";
    prefix ex2;
    grouping leaves {
        uses fr;
        uses es;
    }
    grouping fr {
        leaf feuille {
            type string;
        }
    }
    grouping es {
        leaf hoja {
            type string;
        }
    }
    uses leaves;
}

uses leaves {
    refine "hoja" {
        default "alamo";
    }
}

module example3 {
    namespace "http://example.com/ns/example3";
    prefix ex3;
    typedef dozen {
        type uint8 {
            range 1..12;
        }
    }
    leaf month {
        type dozen;
    }
}

leaf month {
    type dozen {
        range 7..max;
    }
}

module example3bis {
    namespace "http://example.com/ns/example3bis";
    prefix ex3bis;
    typedef dozen {
        type uint8 {
            range 1..12;
        }
        default 7;
    }
    leaf month {
        type dozen;
    }
}

extension documentation-flag {
    argument number;
}

leaf folio {
    acme:documentation-flag 42;
    type string;
}

anyxml data {
    description "Any XML content allowed here.";
}

choice leaves {
    default feuille;
    leaf feuille { type empty; }
    leaf hoja { type empty; }
}

module crypto-base {
  namespace "http://example.com/crypto-base";
  prefix "crypto";
  identity crypto-alg {
    description
      "Base identity from which all crypto algorithms
       are derived.";
    }
}

module des {
  namespace "http://example.com/des";
  prefix "des";
  import "crypto-base" {
    prefix "crypto";
  }
  identity des {
    base "crypto:crypto-alg";
    description "DES crypto algorithm";
  }
  identity des3 {
    base "crypto:crypto-alg";
    description "Triple DES crypto algorithm";
  }
}
   The identities will be mapped to the following named pattern
definitions:

leaf-list foliage {
    min-elements 3;
    max-elements 6378;
    ordered-by user;
    type string;
}

For example, consider the following YANG fragment of a module with
the prefix "yam":
   grouping keygrp {
  leaf clef {
    type uint8;
  }
}
list foo {
  key clef;
  leaf bar {
    type string;
  }
  leaf baz {
    type string;
  }
  uses keygrp;
}

leaf foo {
  type identityref {
    base crypto:crypto-alg;
  }
}

type decimal64 {
    fraction-digits 2;
}

type string {
    length "1|3..8";
    pattern "[A-Z][a-z]*";
}

module example4 {
  namespace "http://example.com/ns/example4";
  prefix ex4;
  uses sorted-leaf-list;
  grouping sorted-leaf-list {
    leaf-list sorted-entry {
      must "not(preceding-sibling::sorted-entry > .)" {
        error-message "Entries must appear in ascending order.";
      }
      type uint8;
    }
  }
}
   The resulting Schematron schema for a reply to <nc:get> is then as
follows:

module example5 {
    namespace "http://example.com/ns/example5";
    prefix ex5;
    choice foobar {
        mandatory true;
        case foo {
            leaf foo1 {
                type uint8;
            }
            leaf foo2 {
                type uint8;
            }
        }
        leaf bar {
            type uint8;
        }
    }
}
   In this module, all three leaf nodes in both case branches are
optional but because of the "mandatory true;" statement, at least one
of them must be present in a valid configuration.  The 'choice'
statement from this module is mapped to the following fragment of the
RELAX NG schema:

module example6 {
  namespace "http://example.com/ns/example6";
  prefix ex6;
  container outer {
    leaf leaf1 {
      type uint8;
      default 1;
    }
    choice one-or-two {
      default "one";
      container one {
        leaf leaf2 {
          type uint8;
          default 2;
        }
      }
      leaf leaf3 {
        type uint8;
        default 3;
      }
    }
  }
}
   The DSRL schema generated for the "get-reply" target document type
will be:

import ietf-yang-types { prefix yang; }
import ietf-inet-types { prefix inet; }
     organization
  "yang-central.org";
description
  "Partial data model for DHCP, based on the config of
   the ISC DHCP reference implementation.";

leaf max-lease-time {
  type uint32;
  units seconds;
  default 7200;
}

leaf default-lease-time {
  type uint32;
  units seconds;
  must '. <= ../max-lease-time' {
    error-message
      "The default-lease-time must be less than max-lease-time";
  }
  default 600;
}

container shared-networks {
  list shared-network {
    key name;

    leaf name {
      type string;
    }
    uses subnet-list;
  }
}

container status {
  config false;
  list leases {
    key address;

      leaf address {
        type inet:ip-address;
      }
      leaf starts {
        type yang:date-and-time;
      }
      leaf ends {
        type yang:date-and-time;
      }
      container hardware {
        leaf type {
          type enumeration {
            enum "ethernet";
            enum "token-ring";
            enum "fddi";
          }
        }
        leaf address {
          type yang:phys-address;
        }
      }
    }
  }
}

grouping subnet-list {
  description "A reusable list of subnets";
  list subnet {
    key net;
    leaf net {
      type inet:ip-prefix;
    }
    container range {
      presence "enables dynamic address assignment";
      leaf dynamic-bootp {
        type empty;
        description
          "Allows BOOTP clients to get addresses in this range";
      }
           leaf low {
        type inet:ip-address;
        mandatory true;
      }
      leaf high {
        type inet:ip-address;
        mandatory true;
      }
    }

container dhcp-options {
  description "Options in the DHCP protocol";
  leaf-list router {
    type inet:host;
    ordered-by user;
    reference "RFC 2132, sec. 3.8";
  }
  leaf domain-name {
    type inet:domain-name;
    reference "RFC 2132, sec. 3.17";
  }
}

      leaf max-lease-time {
        type uint32;
        units seconds;
        default 7200;
      }
    }
  }
}
