contents.text:
policy-options {
    prefix-list IBGP-NEIGHBORS {
        192.0.2.0/24;
    }
    prefix-list EBGP-NEIGHBORS {
        198.51.100.25/32;
        198.51.100.27/32;
        198.51.100.29/32;
        198.51.100.31/32;
    }
    prefix-list RADIUS-SERVERS {
        198.51.100.9/32;
        198.51.100.10/32;
    }
    prefix-list IBGPv6-NEIGHBORS {
        2001:DB8:1::/48;
    }
    prefix-list EBGPv6-NEIGHBORS {
        2001:DB8:100::25/128;
        2001:DB8:100::27/128;
        2001:DB8:100::29/128;
        2001:DB8:100::31/128;
    }
    prefix-list RADIUSv6-SERVERS {
        2001:DB8:100::9/128;
        2001:DB8:100::10/128;
    }
}
   firewall {
    policer 500kbps {
        if-exceeding {
            bandwidth-limit 500k;
            burst-size-limit 1500;
        }
        then discard;
    }
    policer 250kbps {
        if-exceeding {
            bandwidth-limit 250k;
            burst-size-limit 1500;
        }
        then discard;
    }
    family inet {
        filter protect-router-control-plane {
            term first-frag {
                from {
                    first-fragment;
                }
                then {
                    count frag-discards;
                    log;
                    discard;
                }
            }
            term next-frag {
                from {
                    is-fragment;
                }
                then {
                    count frag-discards;
                    log;
                    discard;
                }
            }
            term icmp {
                from {
                    protocol icmp;
                }
                then {
                    policer 500kbps;
                    accept;
                }
            }
               term ospf {
                from {
                    source-address {
                        192.0.2.0/24;
                    }
                    protocol ospf;
                }
                then accept;
            }
            term ibgp-connect {
                from {
                    source-prefix-list {
                        IBGP-NEIGHBORS;
                    }
                    protocol tcp;
                    destination-port bgp;
                }
                then accept;
            }
            term ibgp-reply {
                from {
                    source-prefix-list {
                        IBGP-NEIGHBORS;
                    }
                    protocol tcp;
                    port bgp;
                }
                then accept;
            }
            term ebgp-connect {
                from {
                    source-prefix-list {
                        EBGP-NEIGHBORS;
                    }
                    protocol tcp;
                    destination-port bgp;
                }
                then accept;
            }
               term ebgp-reply {
                from {
                    source-prefix-list {
                        EBGP-NEIGHBORS;
                    }
                    protocol tcp;
                    port bgp;
                }
                then accept;
            }
            term dns {
                from {
                    source-address {
                        198.51.100.0/30;
                    }
                    protocol udp;
                    port domain;
                }
                then accept;
            }
            term ntp {
                from {
                    source-address {
                        198.51.100.4/30;
                    }
                    protocol udp;
                    destination-port ntp;
                }
                then accept;
            }
            term ssh {
                from {
                    source-address {
                        198.51.100.128/25;
                    }
                    protocol tcp;
                    destination-port ssh;
                }
                then accept;
            }
               term snmp {
                from {
                    source-address {
                        198.51.100.128/25;
                    }
                    protocol udp;
                    destination-port snmp;
                }
                then accept;
            }
            term radius {
                from {
                    source-prefix-list {
                        RADIUS-SERVERS;
                    }
                    protocol udp;
                    port [ 1812 1813 ];
                }
                then accept;
            }
            term default-term {
                then {
                    count copp-exceptions;
                    log;
                    policer 500kbps;
                    accept;
                }
            }
        }
    }

family inet6 {
    filter protect-router-control-plane-v6 {
        term fragv6 {
            from {
                next-header fragment;
            }
            then {
                count frag-v6-discards;
                log;
                discard;
            }
        }
               term icmpv6 {
            from {
                next-header icmpv6;
            }
            then {
                policer 500kbps;
                accept;
            }
        }
        term ospfv3 {
            from {
                source-address {
                    FE80::/10;
                }
                next-header ospf;
            }
            then accept;
        }
        term ibgpv6-connect {
            from {
                source-prefix-list {
                    IBGPv6-NEIGHBORS;
                }
                next-header tcp;
                destination-port bgp;
            }
            then accept;
        }
        term ibgpv6-reply {
            from {
                source-prefix-list {
                    IBGPv6-NEIGHBORS;
                }
                next-header tcp;
                port bgp;
            }
            then accept;
        }
        term ebgpv6-connect {
            from {
                source-prefix-list {
                    EBGPv6-NEIGHBORS;
                }
                next-header tcp;
                destination-port bgp;
            }
            then accept;
        }
               term ebgpv6-reply {
            from {
                source-prefix-list {
                    EBGPv6-NEIGHBORS;
                }
                next-header tcp;
                port bgp;
            }
            then accept;
        }
        term dnsv6 {
            from {
                source-address {
                   2001:DB8:100:1::/64;
                   }
                next-header [ udp tcp ];
                port domain;
            }
            then accept;
        }
        term ntpv6 {
            from {
                source-address {
                    2001:DB8:100:2::/64;
                }
                next-header udp;
                destination-port ntp;
            }
            then accept;
        }
        term sshv6 {
            from {
                source-address {
                    2001:DB8:100:3::/64;
                }
                next-header tcp;
                destination-port ssh;
            }
            then accept;
        }
               term snmpv6 {
            from {
                source-address {
                    2001:DB8:100:3::/64;
                }
                next-header udp;
                destination-port snmp;
            }
            then accept;
        }
        term radiusv6 {
            from {
                source-prefix-list {
                    RADIUSv6-SERVERS;
                }
                next-header udp;
                port [ 1812 1813 ];
            }
            then accept;
        }
        term default-term-v6 {
            then {
                policer 500kbps;
                count copp-exceptions-v6;
                log;
                accept;
            }
        }
    }
}

    family any {
        filter protect-router-control-plane-non-ip {
            term rate-limit-non-ip {
                then {
                    policer 250kbps;
                    accept;
                }
            }
        }
    }
}
   interfaces {
    lo0 {
        unit 0 {
            family inet {
                filter input protect-router-control-plane;
            }
            family inet6 {
                filter input protect-router-control-plane-v6;
            }
            family any {
                filter input protect-router-control-plane-non-ip;
            }
        }
    }
}
