  reference
    "RFC 8343: A YANG Data Model for Interface Management";
}
import ietf-routing {
  prefix rt;
  reference
    "RFC 8349: A YANG Data Model for Routing Management (NMDA
     Version)";
}
import ietf-pim-base {
  prefix pim-base;
  reference
    "RFC 9128: A YANG Data Model for Protocol Independent
     Multicast (PIM)";
}
import ietf-pim-rp {
  prefix pim-rp;
  reference
    "RFC 9128: A YANG Data Model for Protocol Independent
