    description
      "Value of the metric.";
  }
  description
    "Global default metric configuration grouping.";
}

grouping overload-global-cfg {
  leaf status {
    type boolean;
    default "false";
    description
      "This leaf specifies the overload status.";
  }
  description
    "Grouping for overload bit configuration.";
}

grouping overload-max-metric-global-cfg {
