      leaf ipv4-router-id {
        type inet:ipv4-address;
        description
          "Router ID value that would be used in TLV 134.";
      }
      leaf ipv6-router-id {
        type inet:ipv6-address;
        description
          "Router ID value that would be used in TLV 140.";
      }
    }
    container ldp {
      container igp-sync {
        if-feature "ldp-igp-sync";
        description
          "This container may be augmented with global
           parameters for LDP IGP synchronization.";
      }
      description
        "LDP configuration.";
    }
    description
      "MPLS configuration.";
  }
  description
    "Grouping for MPLS global parameters.";
}

grouping lsp-parameters {
  leaf lsp-mtu {
    type uint16;
    units "bytes";
    default "1492";
    description
      "Maximum size of an LSP PDU in bytes.";
