      type rt-types:bandwidth-ieee-float32;
      units "Bps";
      description
        "Residual bandwidth.";
    }
  }
  container unidirectional-link-available-bandwidth {
    description
      "Container for the available bandwidth
       from the local neighbor to the remote neighbor.";
    leaf value {
      type rt-types:bandwidth-ieee-float32;
      units "Bps";
      description
        "Available bandwidth.";
    }
