{ id-tc26-sign gost3410-12-256(1) }

-- GOST R 34.10-2012 / 512-bit signature algorithm
id-tc26-gost3410-12-512 OBJECT IDENTIFIER ::=
{ id-tc26-sign gost3410-12-512(2) }

-- GOST R 34.11-2012 / 256-bit hash algorithm
